    private fun initializeMovables(tile: Size) {
        movable.add(Movable(ItemName.EMPTY, IType.SOLID, Size(50, 40),
                VectorD(570.0, 720.0), tile))
        movable.add(Movable(ItemName.EMPTY, IType.SOLID, Size(180, 80),
                VectorD(1160.0, 680.0), tile))
        movable.add(Movable(ItemName.EMPTY, IType.SOLID, Size(20, 20),
                VectorD(1480.0, 500.0), tile))
    }

    private fun initializeMMovables(tile: Size) {
        movableWalls.add(MovableWall({list -> list[0]}, Size(10, 90), VectorD(750.0, 110.0),
                tile, Size(0, 0), VectorInt(0, 1)))
        movableWalls.add(MovableWall({list -> list[1] xor list[0]}, Size(10, 110),
                VectorD(1490.0, 650.0),
                tile, Size(0,0), VectorInt(0, 1)))
    }

    private fun initializeMechanisms(tile: Size) {
        mechanisms.add(Mechanism(Size(10, 1), VectorD(30.0, 98.0), tile))
        mechanisms.add(Mechanism(Size(10, 1), VectorD(560.0, 198.0), tile))
        mechanisms.add(Mechanism(Size(10, 1), VectorD(2280.0, 78.0), tile))

        movableWalls[0].addMechanism(mechanisms[0]) { bool -> bool }
        movableWalls[1].addMechanism(mechanisms[1]) { bool -> bool }
        movableWalls[1].addMechanism(mechanisms[2]) { bool -> bool }
    }

    private fun initializeFragments(tile: Size) {
        fragments.add(Fragment(Ability.OBJECT_MANIPULATION, VectorD(30.0, 50.0), tile))
        fragments.add(Fragment(Ability.OBJECT_MANIPULATION, VectorD(1430.0, 570.0), tile))
    }

    fun addDoors(levels: HashMap<LevelName, Level>) {
        doors[0].nextLevel = levels[LevelName.LVL2] as Level
        doors[1].nextLevel = levels[LevelName.LVL3] as Level
    }

    fun setConnectedDoors(levels: HashMap<LevelName, Level>) {
        doors[0].connectedDoor = (levels[LevelName.LVL2] as Lvl2).doors[0]
        doors[1].connectedDoor = (levels[LevelName.LVL3] as Lvl3).doors[0]
        setExitPos()
    }

    private fun setExitPos() {
        doors[0].exitPos = VectorD(30.0, 750.0)
        doors[1].exitPos = VectorD((map[0].size - 2) * 20.0 + 10.0, 750.0)
    }
